
public class Ejercicio1 {
	
	public static void main(String[] args) {
		
		int valor1 = 15;
		int valor2 = 4;
		
		// TODO Auto-generated method stub
		System.out.println("La suma de las variables es igual a: " + (valor1 + valor2));
		System.out.println("La resta de las variables es igual a : " + (valor1 - valor2));
		System.out.println("La multiplación de las variables es igual a:  " + (valor1 * valor2));
		System.out.println("La división de las variables es igual a : " + ((double) valor1 / (double) valor2));
		System.out.println("El resto de la división de las variables es igual a : " + ( valor1 % valor2 ));
	}

}

